INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (1, '周冬雨/易烊千玺/尹昉/周也/吴越', NULL, '少年的你，如此美丽', 'https://img2.doubanio.com/view/photo/l/public/p2555837432.webp', '曾国祥', NULL, '8.2', '1377476', '剧情/爱情/犯罪', 'tt9586294', '汉语普通话', '135', '30166972', '少年的你', NULL, '中国大陆/中国香港', '2019-10-25', NULL, '陈念（周冬雨 饰）是一名即将参加高考的高三学生，同校女生胡晓蝶（张艺凡 饰）的跳楼自杀让她的生活陷入了困顿之中。胡晓蝶死后，陈念遭到了以魏莱（周也 饰）为首的三人组的霸凌，魏莱虽然表面上看来是乖巧的优等生，实际上却心思毒辣，胡晓蝶的死和她有着千丝万缕的联系。 一次偶然中，陈念邂逅了名为小北（易烊千玺 饰）的小混混，随着时间的推移，心心相惜的两人之间产生了真挚的感情。小北答应陈念在暗中保护她免受魏莱的欺凌，没想到这一决定引发了一连串的连锁反应。负责调查胡晓蝶死因的警官郑易（尹昉 饰）隐隐察觉到校园里的古怪气氛，可他的调查却屡屡遭到校方的阻挠。', NULL, '2019');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (2, '杨紫琼?/?许玮伦?/?关继威?/?吴汉章?/?杰米·李·柯蒂斯', NULL, '奇异女侠玩救宇宙', 'https://img9.doubanio.com/view/photo/l/public/p2873634834.webp', '关家永 / 丹尼尔·施纳特', NULL, '7.7', '337711', '喜剧/奇幻/冒险', 'tt6710474', '英语/汉语普通话/粤语', '139', '30314848', '瞬息全宇宙', NULL, '美国', '2022-3-11', NULL, '55岁的美国华裔移民伊芙琳（杨紫琼 饰）家庭事业两崩溃。在老父亲生日大寿这天，除了要阻止女儿（许玮伦 饰）暴走出柜、替无用丈夫（关继威 饰）擦屁股，还得去税务局向古板大婶（杰米·李·柯蒂斯 饰）解释不太妙的税务问题。才踏进大楼电梯，软烂丈夫竟摇身一变演起黑客帝国，声称自己是另一个宇宙的版本，而秀莲也是千万宇宙里的其中一个。还来不及理解，邪恶势力已在多元宇宙中蔓延，世界即将毁灭，只有在这个宇宙里，“一无是处”的伊芙琳才能拯救世界', NULL, '2022');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (3, '金妮弗·古德温/杰森·贝特曼/伊德里斯·艾尔巴/珍妮·斯蕾特/内特·托伦斯', NULL, '动物乌托邦', 'https://img1.doubanio.com/view/photo/l/public/p2869826228.webp', '拜伦·霍华德/瑞奇·摩尔/杰拉德·布什', NULL, '9.2', '1744023', '喜剧/动画/冒险', 'tt2948356', '英语/挪威语', '109', '25662329', '疯狂动物城', NULL, '美国', '2016-3-4', NULL, '故事发生在一个所有哺乳类动物和谐共存的美好世界中，兔子朱迪（金妮弗·古德温 Ginnifer Goodwin 配音）从小就梦想着能够成为一名惩恶扬善的刑警，凭借着智慧和努力，朱迪成功的从警校中毕业进入了疯狂动物城警察局，殊不知这里是大型肉食类动物的领地，作为第一只，也是唯一的小型食草类动物，朱迪会遇到怎样的故事呢？近日里，城中接连发生动物失踪案件，就在全部警员都致力于调查案件真相之时，朱迪却被局长（伊德瑞斯·艾尔巴 Idris Elba 配音）发配成为了一名无足轻重的交警。某日，正在执勤的兔子遇见了名为尼克（杰森·贝特曼 Jason Bateman 配音）的狐狸，两人不打不相识，之后又误打误撞的接受了寻找失踪的水獭先生的任务，如果不能在两天之内找到水獭先生，朱迪就必须自愿离开警局。朱迪找到了尼克，两人联手揭露了一个隐藏在疯狂动物城之中的惊天秘密。', NULL, '2016');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (4, '徐峥/王传君/周一围/谭卓/章宇', NULL, '中国药神', 'https://img1.doubanio.com/view/photo/l/public/p2519432317.webp', '文牧野', NULL, '9', '1933541', '剧情/喜剧', 'tt7362036', '汉语普通话/英语/上海话/印地语', '117', '26752088', '我不是药神', NULL, '中国', '2018-7-5', NULL, '普通中年男子程勇（徐峥 饰）经营着一家保健品店，失意又失婚。不速之客吕受益（王传君 饰）的到来，让他开辟了一条去印度买药做“代购”的新事业，虽然困难重重，但他在这条“买药之路”上发现了商机，一发不可收拾地做起了治疗慢粒白血病的印度仿制药独家代理商。赚钱的同时，他也认识了几个病患及家属，为救女儿被迫做舞女的思慧（谭卓 饰）、说一口流利“神父腔”英语的刘牧师（杨新鸣 饰），以及脾气暴烈的“黄毛”（章宇 饰），几个人合伙做起了生意，利润倍增的同时也危机四伏。程勇昔日的小舅子曹警官（周一围 饰）奉命调查仿制药的源头，假药贩子张长林（王砚辉 饰）和瑞士正牌医药代表（李乃文 饰）也对其虎视眈眈，生意逐渐变成了一场关于救赎的拉锯战。', NULL, '2018');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (5, '安东尼·冈萨雷斯/盖尔·加西亚·贝纳尔/本杰明·布拉特/阿兰娜·乌巴赫/芮妮·维克托', NULL, '玩转极乐园', 'https://img1.doubanio.com/view/photo/l/public/p2603173208.webp', '李·昂克里奇/阿德里安·莫利', NULL, '9.1', '1505053', '喜剧/动画/音乐/奇幻', 'tt2380307', '英语 / 西班牙语', '105', '20495023', '寻梦环游记', NULL, '美国', '2017-11-24', NULL, '热爱音乐的米格尔（安东尼·冈萨雷兹 Anthony Gonzalez 配音）不幸地出生在一个视音乐为洪水猛兽的大家庭之中，一家人只盼着米格尔快快长大，好继承家里传承了数代的制鞋产业。一年一度的亡灵节即将来临，每逢这一天，去世的亲人们的魂魄便可凭借着摆在祭坛上的照片返回现世和生者团圆。 在一场意外中，米格尔竟然穿越到了亡灵国度之中，在太阳升起之前，他必须得到一位亲人的祝福，否则就将会永远地留在这个世界里。米格尔决定去寻找已故的歌神德拉库斯（本杰明·布拉特 Benjamin Bratt 配音），因为他很有可能就是自己的祖父。途中，米格尔邂逅了落魄乐手埃克托（盖尔·加西亚·贝纳尔 Gael García Bernal 配音），也渐渐发现了德拉库斯隐藏已久的秘密。', NULL, '2017');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (6, '柊瑠美/入野自由/夏木真理/菅原文太/中村彰男', NULL, '神隐少女', 'https://img3.doubanio.com/view/photo/l/public/p2557573350.webp', '宫崎骏', NULL, '9.4', '2064332', '剧情/动画/奇幻', 'tt0245429', '日语', '125', '1291561', '千与千寻', NULL, '日本', '2019-6-21', NULL, '千寻和爸爸妈妈一同驱车前往新家，在郊外的小路上不慎进入了神秘的隧道——他们去到了另外一个诡异世界—一个中世纪的小镇。远处飘来食物的香味，爸爸妈妈大快朵颐，孰料之后变成了猪！这时小镇上渐渐来了许多样子古怪、半透明的人。 千寻仓皇逃出，一个叫小白的人救了他，喂了她阻止身体消 失的药，并且告诉她怎样去找锅炉爷爷以及汤婆婆，而且必须获得一份工作才能不被魔法变成别的东西。 千寻在小白的帮助下幸运地获得了一份在浴池打杂的工作。渐渐她不再被那些怪模怪样的人吓倒，并从小玲那儿知道了小白是凶恶的汤婆婆的弟子。 一次，千寻发现小白被一群白色飞舞的纸人打伤，为了救受伤的小白，她用河神送给她的药丸驱出了小白身体内的封印以及守封印的小妖精，但小白还是没有醒过来。 为了救小白，千寻又踏上了她的冒险之旅。', NULL, '2019');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (7, '神木隆之介/上白石萌音/长泽雅美/市原悦子/成田凌', NULL, '君之名', 'https://img9.doubanio.com/view/photo/l/public/p2396582404.webp', '新海诚', NULL, '8.5', '1273819', '剧情/爱情/动画', 'tt5311514', '日语', '106', '26683290', '你的名字。', NULL, '日本', '2016-12-2', NULL, '在远离大都会的小山村，住着巫女世家出身的高中女孩宫水三叶（上白石萌音 配音）。校园和家庭的原因本就让她充满烦恼，而近一段时间发生的奇怪事件，又让三叶摸不清头脑。不知从何时起，三叶在梦中就会变成一个住在东京的高中男孩。那里有陌生的同学和朋友，有亲切的前辈和繁华的街道，一切都是如此诱人而真实。另一方面，住在东京的高中男孩立花泷（神木隆之介 配音）则总在梦里来到陌生的小山村，以女孩子的身份过着全新的生活。许是受那颗神秘彗星的影响，立花和三叶在梦中交换了身份。他们以他者的角度体验着对方的人生，这期间有愤怒、有欢笑也有暖心。只是两人并不知道，身份交换的背后隐藏着重大而锥心的秘密', NULL, '2016');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (8, '泰伊·谢里丹/奥利维亚·库克/本·门德尔森/马克·里朗斯/丽娜·维特', NULL, '玩家一号', 'https://img2.doubanio.com/view/photo/l/public/p2516306833.webp', '史蒂文·斯皮尔伯格', NULL, '8.7', '1260221', '动作/科幻/冒险', 'tt1677720', '英语/日语/汉语普通话', '140', '4920389', '头号玩家', NULL, '美国', '2018-3-30', NULL, '故事发生在2045年，虚拟现实技术已经渗透到了人类生活的每一个角落。詹姆斯哈利迪（马克·里朗斯 Mark Rylance 饰）一手建造了名为“绿洲”的虚拟现实游戏世界，临终前，他宣布自己在游戏中设置了一个彩蛋，找到这枚彩蛋的人即可成为绿洲的继承人。要找到这枚彩蛋，必须先获得三把钥匙，而寻找钥匙的线索就隐藏在詹姆斯的过往之中。 韦德（泰尔·谢里丹 Tye Sheridan 饰）、艾奇（丽娜·维特 Lena Waithe 饰）、大东（森崎温 饰）和修（赵家正 饰）是游戏中的好友，和之后遇见的阿尔忒弥斯（奥利维亚·库克 Olivia Cooke 饰）一起，五人踏上了寻找彩蛋的征程。他们所要对抗的，是名为诺兰索伦托（本·门德尔森 Ben Mendelsohn 饰）的大资本家。', NULL, '2018');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (9, '吕艳婷/囧森瑟夫/瀚墨/陈浩/绿绮', NULL, '哪吒降世', 'https://img3.doubanio.com/view/photo/l/public/p2562790910.webp', '饺子', NULL, '8.4', '1718343', '剧情/喜剧/动画/奇幻', 'tt10627720', '汉语普通话', '110', '26794435', '哪吒之魔童降世', NULL, '中国大陆', '2019-7-26', NULL, '天地灵气孕育出一颗能量巨大的混元珠，元始天尊将混元珠提炼成灵珠和魔丸，灵珠投胎为人，助周伐纣时可堪大用；而魔丸则会诞出魔王，为祸人间。元始天尊启动了天劫咒语，3年后天雷将会降临，摧毁魔丸。太乙受命将灵珠托生于陈塘关李靖家的儿子哪吒身上。然而阴差阳错，灵珠和魔丸竟然被掉包。本应是灵珠英雄的哪吒却成了混世大魔王。调皮捣蛋顽劣不堪的哪吒却徒有一颗做英雄的心。然而面对众人对魔丸的误解和即将来临的天雷的降临，哪吒是否命中注定会立地成魔？他将何去何从？', NULL, '2019');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (10, '沈腾/宋芸桦/张一鸣/张晨光/常远', NULL, 'Hello Mr. Billionaire', 'https://img2.doubanio.com/view/photo/l/public/p2529206743.webp', '闫非/彭大魔', NULL, '6.6', '950042', '喜剧', 'tt8529186', '汉语普通话', '118', '27605698', '西虹市首富', NULL, '中国大陆', '2018-7-27', NULL, '西虹市丙级球队大翔队的守门员王多鱼（沈腾 饰）因比赛失利被教练开除，一筹莫展之际王多鱼突然收到神秘人士金老板（张晨光 饰）的邀请，被告知自己竟然是保险大亨王老太爷（李立群 饰）的唯一继承人，遗产高达百亿！但是王老太爷给出了一个非常奇葩的条件，那就是要求王多鱼在一个月内花光十亿，还不能告诉身边人，否则失去继承权。王多鱼毫不犹豫签下了“军令状”，与好友庄强（张一鸣 饰）以及财务夏竹（宋芸桦 饰）一起开启了“挥金之旅”，即将成为西虹市首富的王多鱼，第一次感受到了做富人的快乐，同时也发现想要挥金如土实在没有那么简单！', NULL, '2018');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (11, '爱德华·阿斯纳/克里斯托弗·普卢默/乔丹·长井', NULL, NULL, 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2363116942.webp', '彼特·道格特/鲍勃·彼德森', NULL, '9.1', NULL, '剧情/喜剧/动画/冒险', NULL, '英语', '96', '2129039', '飞屋环游记', NULL, '美国', '2009-8-4', NULL, '小男孩卡尔（Carl Fredricksen）怀揣着对于冒险的热爱偶遇假小子艾丽（Ellie），而艾丽把整个屋子当成一艘大飞船游戏居然使他对这个女孩子有些着迷，相同的爱好最终使两个人成为了一生的爱侣。他们有一个梦想，那就是有朝一日要去南美洲的“仙境瀑布”探险，但直到艾丽去世，这个梦想也未能实现。终于有一天，曾经专卖气球的老人卡尔居然用五颜六色的气球拽着他的房子飞上了天空，他决定要去实现他们未曾实现的梦想。令卡尔始料不及的是，门廊居然搭上了一个自称是“荒野开拓者”的小男孩小罗（Russell），小罗的喋喋不休让卡尔对这个小胖墩格外讨厌。一老一少在飞行中经过了千难万险终于看到了传说中的“仙境瀑布”，在相处过程中卡尔发现小罗其实是个惹人怜爱的孩子。在步行穿越一座森林时，他们遇到了不会飞的大鸟凯文（Kevin）和一只会说话的狗狗逗逗（Dug），让老人惊讶的是他们还遇到了他少年的崇拜偶像——探险家查尔斯·蒙兹（Charles Muntz），而且他发现蒙兹居然是一个为达目的不择手段的坏人。这时，老人离自己的梦想之地只有一步之遥……', NULL, '2009');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (12, '罗伯托·贝尼尼/尼可莱塔·布拉斯基/乔治·坎塔里尼', NULL, NULL, 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2578474613.webp', '罗伯托·贝尼尼', NULL, '9.6', NULL, '剧情/喜剧/爱情/战争', NULL, '意大利语 / 德语 / 英语', '116', '1306029', '美丽人生', NULL, '意大利', '2020-1-3', NULL, '犹太青年圭多（罗伯托·贝尼尼）邂逅美丽的女教师多拉（尼可莱塔·布拉斯基），他彬彬有礼的向多拉鞠躬：“早安！公主！”。历经诸多令人啼笑皆非的周折后，天遂人愿，两人幸福美满的生活在一起。然而好景不长，法西斯政权下，圭多和儿子被强行送往犹太人集中营。多拉虽没有犹太血统，毅然同行，与丈夫儿子分开关押在一个集中营里。聪明乐天的圭多哄骗儿子这只是一场游戏，奖品就是一辆大坦克，儿子快乐、天真的生活在纳粹的阴霾之中。尽管集中营的生活艰苦寂寞，圭多仍然带给他人很多快乐，他还趁机在纳粹的广播里问候妻子：“早安！公主！”法西斯政权即将倾覆，纳粹的集中营很快就要接受最后的清理，圭多编给儿子的游戏该怎么结束？他们一家能否平安的度过这黑暗的年代呢？', NULL, '2020');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (13, '马修·麦康纳/安妮·海瑟薇/杰西卡·查斯坦', NULL, NULL, 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2614988097.webp', '克里斯托弗·诺兰', NULL, '9.4', NULL, '剧情/科幻/冒险', NULL, '英语', '169', '1889243', '星际穿越', NULL, '美国/英国/加拿大', '2014-11-12', NULL, '近未来的地球黄沙遍野，小麦、秋葵等基础农作物相继因枯萎病灭绝，人类不再像从前那样仰望星空，放纵想象力和灵感的迸发，而是每日在沙尘暴的肆虐下倒数着所剩不多的光景。在家务农的前NASA宇航员库珀（马修·麦康纳 Matthew McConaughey 饰）接连在女儿墨菲（麦肯吉·弗依 Mackenzie Foy 饰）的书房发现奇怪的重力场现象，随即得知在某个未知区域内前NASA成员仍秘密进行一个拯救人类的计划。多年以前土星附近出现神秘虫洞，NASA借机将数名宇航员派遣到遥远的星系寻找适合居住的星球。在布兰德教授（迈克尔·凯恩 Michael Caine 饰）的劝说下，库珀忍痛告别了女儿，和其他三名专家教授女儿艾米莉亚·布兰德（安妮·海瑟薇 Anne Hathaway 饰）、罗米利（大卫·吉雅西 David Gyasi 饰）、多伊尔（韦斯·本特利 Wes Bentley 饰）搭乘宇宙飞船前往目前已知的最有希望的三颗星球考察。他们穿越遥远的星系银河，感受了一小时七年光阴的沧海桑田，窥见了未知星球和黑洞的壮伟与神秘。在浩瀚宇宙的绝望而孤独角落，总有一份超越了时空的笃定情怀将他们紧紧相连……', NULL, '2014');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (14, '吴京/屈楚萧/李光洁/吴孟达', NULL, NULL, 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2545472803.webp', '郭帆', NULL, '7.9', NULL, '科幻/冒险/灾难', NULL, '汉语普通话 / 英语 / 俄语 / 法语 / 日语 / 韩语 / 印尼语', '125', '26266893', '流浪地球', NULL, '中国大陆', '2019-2-5', NULL, '近未来，科学家们发现太阳急速衰老膨胀，短时间内包括地球在内的整个太阳系都将被太阳所吞没。为了自救，人类提出一个名为“流浪地球”的大胆计划，即倾全球之力在地球表面建造上万座发动机和转向发动机，推动地球离开太阳系，用2500年的时间奔往另外一个栖息之地。中国航天员刘培强（吴京 饰）在儿子刘启四岁那年前往国际空间站，和国际同侪肩负起领航者的重任。转眼刘启（屈楚萧 饰）长大，他带着妹妹朵朵（赵今麦 饰）偷偷跑到地表，偷开外公韩子昂（吴孟达 饰）的运输车，结果不仅遭到逮捕，还遭遇了全球发动机停摆的事件。为了修好发动机，阻止地球坠入木星，全球开始展开饱和式营救，连刘启他们的车也被强征加入。在与时间赛跑的过程中，无数的人前仆后继，奋不顾身，只为延续百代子孙生存的希望……', NULL, '2019');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (15, '苏拉·沙玛/伊尔凡·可汗/拉菲·斯波/阿迪勒·侯赛因', NULL, NULL, 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p1784592701.webp', '李安', NULL, '9.1', NULL, '剧情/奇幻/冒险', NULL, '英语/泰米尔语/法语/日语/印地语/汉语普通话', '127', '1929463', '少年派的奇幻漂流', NULL, '美国/中国台湾/英国/加拿大', '2012-11-22', NULL, '故事开始于蒙特娄，也结束于蒙特娄。一名在找寻灵感的作家（拉菲·斯波 Rafe Spall 饰）无意间得知派·帕帖尔（伊尔凡·可汗 Irrfan Khan 饰）的传奇故事。派的父亲（阿迪勒·侯赛因 Adil Hussain 饰）开了一家动物园。因这样特殊的生活环境，少年派（苏拉·沙玛 Suraj Sharma 饰 ）对信仰与人的本性自有一套看法。在派17岁那一年，他的父母决定举家移民加拿大以追求更好的生活，而他也必须离开他的初恋情人。在前往加拿大的船上，他们遇见一位残忍成性的法国厨师（杰拉尔·德帕迪约 Gérard Depardieu 饰）。当天深夜在茫茫大海中，原本令派感到刺激无比的暴风雨一瞬间就成了吞噬货船的大灾难。派却奇迹般地活了下来，搭着救生船在太平洋上漂流，而且有一名最令人意想不到的同伴——理查德·帕克，一只孟加拉老虎。神奇的冒险旅程就这样意外开始了……', NULL, '2012');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (16, '约翰·古德曼/比利·克里斯托/玛丽·吉布斯', NULL, NULL, 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2513247938.webp', '彼特·道格特/大卫·西尔弗曼/李·昂克里奇', NULL, '8.8', NULL, '喜剧/动画/儿童/奇幻/冒险', NULL, '英语', '92', '1291579', '怪兽电力公司', NULL, '美国', '2001-11-2', NULL, '我们不知道，在那样的世界里住着那样一群怪物。他们长相滑稽，却必须装作狰狞。因为，在夜深的时候，他们要悄悄的出现在孩子的衣橱里，吓唬刚刚甜美睡去的宝宝。一切，只因为他们是怪物电力公司的员工，而孩子的尖叫，则是怪物王国发电的全部能量。怪物们害怕孩子，却又必须让孩子尖叫。在怪物的国度里，他们都是为全民造福的英雄。毛怪苏利文（约翰?古德曼）是怪物公司最出色的员工，业绩总是摇摇领先，他吓哭的小孩儿不计其数。他与搭档大眼怪麦克（比利?克里斯托）很受大家的爱戴。一次偶然，毛怪不小心把一个两岁的小女孩阿布（玛丽?吉布斯）带回了怪物世界，引起了极大的恐慌，调皮可爱的阿布把这些可怕的怪物吓的人仰马翻。警方在追捕阿布，而坏蛋亨利（詹姆斯?柯博）也计划利用阿布进行他改革工厂的邪恶计划。另一方面，在与阿布相处的过程中，毛怪渐渐对她产生莫名的情愫，甚至因此被放逐野外。可是一切都不能抵挡毛怪拯救女孩的信念，为了阿布，毛怪踏上了艰险的旅程……', NULL, '2001');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (17, '马特·达蒙 / 杰西卡·查斯坦 / 克里斯汀·韦格 / 杰夫·丹尼尔斯 / 迈克尔·佩纳 / 凯特·玛拉 / 肖恩·宾 / 塞巴斯蒂安·斯坦 / 唐纳德·格洛弗 / 切瓦特·埃加福 / 阿卡塞尔·亨涅 / 陈数 / 高雄 / 本尼迪克特·王 / 麦肯兹·戴维斯 / 娜奥米·斯科特 / 丽丽·博尔丹 / 布莱恩·卡斯佩', NULL, NULL, 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2280097442.webp', '雷德利·斯科特', NULL, '8.5', NULL, '剧情/科幻/冒险', NULL, '英语/汉语普通话', '144', '25864085', '火星救援', NULL, '英国/美国/匈牙利/约旦', '2015-11-25', NULL, '载人航天宇宙飞船阿瑞斯3号成功抵达火星，谁知一场破坏力极其巨大的风暴向宇航员们袭来，阿瑞斯3号被迫中断任务，紧急返航。撤离途中，宇航员马克·沃特尼（马特·达蒙 Matt Damon 饰）被飞船上吹落的零件击中，由于生还希望渺茫，队友们只得匆匆返航，并向世人宣告他已牺牲的事实。出乎意料的是，马克以极低的概率活了下来。他躲进驻火星的航天基地疗伤。下一次火星任务要等到四年后，而基地内的补给仅够他维持31天。短暂的绝望后，马克决定利用有限的食物，在这颗空无一人的星球上种植作物，寻找一线生机。与此同时，地球方面也很快发现马克生还的事实，他们想尽办法部署营救计划。孤军奋战的马克，能否等到救援队伍的到来？\n　　影片改编自安迪·威尔的同名科幻小说。', NULL, '2014');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (18, '克里斯蒂安·贝尔/倪妮/张歆怡/黄天元', NULL, NULL, 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p1322247572.webp', '张艺谋', NULL, '8.3', NULL, '剧情/历史/战争', NULL, '汉语普通话 / 英语 / 日语 / 南京话 / 上海话', '145', '3649049', '金陵十三钗', NULL, '中国大陆/中国香港', '2011-12-15', NULL, '1937年，日军入侵南京，战火中，六朝古都化为废墟，众多中国军民被困城中。一支十数人的国军德械教导队余部在长官李教官（佟大为 饰）指挥下，从日军手中救出了一批教会学校女学生，而李教官等人，至此丧失了出城的机会。幸免于难的书娟（张歆怡 饰）等学生返回文彻斯特教堂，随她们一 起来到的，还有受雇远道至此收敛神父遗体的美国人约翰·米勒（克里斯蒂安·贝尔 Christian Bale 饰）。此时的南京城中，逃难的人们蜂拥进安全区和教堂寻找庇护，十四名风尘女子强行进入教堂避难，其中精通英语的玉墨（倪妮 饰）希望借助约翰的身份为一行人提供更多保护。教堂的墙壁，并不能阻挡日军的铁蹄，教堂内的人们，将面临孰生孰死的选择……', NULL, '2011');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (19, '秦昊/王景春/荣梓杉/史彭元', NULL, NULL, 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2609064048.webp', '辛爽', NULL, '8.8', NULL, '剧情/悬疑/犯罪', NULL, '汉语普通话', '50', '33404425', '隐秘的角落', NULL, '中国大陆', '2020-6-16', NULL, '该剧改编自紫金陈推理小说《坏小孩》 ，讲述了沿海小城的三个孩子在景区游玩时无意拍摄记录了一次谋杀，他们的冒险也由此展开。扑朔迷离的案情，将几个家庭裹挟其中，带向不可预知的未来......', NULL, '2020');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (20, '阿尔·帕西诺/罗伯特·杜瓦尔/黛安·基顿/罗伯特·德尼罗', NULL, NULL, 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2194138787.webp', '弗朗西斯·福特·科波拉', NULL, '9.2', NULL, '剧情/犯罪', NULL, '英语/意大利语/西班牙语/拉丁语/西西里语', '202', '1299131', '教父2', NULL, '美国', '1974-12-12', NULL, '迈克尔（阿尔·帕西诺 饰）是美利坚黑手党科利昂家族的头目。 迈克尔的父亲维托·安多里尼（罗伯特·德尼罗 饰）出生于意大利科利昂镇。1901年，维托的父亲安东尼奥、兄长保罗、母亲（玛丽亚·卡塔 饰）都死于黑手党西乔家族之手。9岁的维托只得漂洋过海逃亡美利坚谋生。入境时，海关官员（理查德·沃森 饰）将其名姓登记为维托·科利昂。 1958年，迈克尔为儿子安东尼（詹姆斯·古纳瑞斯 饰）举办了初领圣体的聚会，不想当晚便遭遇暗杀。迈克尔侥幸躲过一劫，两名杀手却被人灭口。 维托在异国他乡如何生存？他身负的血海深仇能否得报？想要迈克尔性命的是谁？将杀手灭口的又是谁？', NULL, '1974');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (21, '本尼迪克特·康伯巴奇/伊丽莎白·奥尔森/切瓦特·埃加福/本尼迪克特·王', NULL, NULL, 'https://img1.doubanio.com/view/photo/l/public/p2872114449.webp', '山姆·雷米', NULL, '6.4', NULL, '动作/恐怖/奇幻/冒险', NULL, '英语', '126', '30304994', '疯狂多元宇宙', NULL, '美国', '2022-5-4', NULL, '充满无限未知的疯狂多元宇宙即将展开，一切皆有可能。', NULL, '2022');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (22, '克里斯·帕拉特/布莱丝·达拉斯·霍华德/劳拉·邓恩/山姆·尼尔', NULL, NULL, 'https://img1.doubanio.com/view/photo/l/public/p2873135507.webp', '科林·特莱沃若', NULL, '6.3', NULL, '动作/科幻/冒险', NULL, '英语', '146', '26873582', '侏罗纪世界3', NULL, '美国', '2022-6-10', NULL, '影片为《侏罗纪世界》系列的完结篇，故事的开篇设定在纳布拉尔岛被摧毁的四年后。如今，恐龙在世界各地与人类共同生活、共同捕猎。这一脆弱的平衡将重塑未来，并最终决定人类能否与史上最可怕生物共享这颗星球，并继续站在食物链的顶端。', NULL, '2022');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (23, '让·雷诺/娜塔莉·波特曼/加里·奥德曼/丹尼·爱罗/彼得·阿佩尔', NULL, NULL, 'https://img2.doubanio.com/view/photo/l/public/p511118051.webp', '吕克·贝松', NULL, '9.4', NULL, '剧情/动作/犯罪', NULL, '英语/意大利语/法语', '110', '1295644', '这个杀手不太冷', NULL, '法国/美国', '1994-9-14', NULL, '里昂（让·雷诺饰）是名孤独的职业杀手，受人雇佣。一天，邻居家小姑娘马蒂尔达（纳塔丽·波特曼饰)敲开他的房门，要求在他那里暂避杀身之祸。原来邻居家的主人是警方缉毒组的眼线，只因贪污了一小包毒品而遭恶警（加里·奥德曼饰）杀害全家的惩罚。马蒂尔达得到里昂的留救，幸免于难，并留在里昂那里。里昂教小女孩使枪，她教里昂法文，两人关系日趋亲密，相处融洽。女孩想着去报仇，反倒被抓，里昂及时赶到，将女孩救回。混杂着哀怨情仇的正邪之战渐次升级，更大的冲突在所难免……', NULL, '1994');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (24, '朱苏进/述平/姜文/郭俊立/危笑', NULL, NULL, 'https://img1.doubanio.com/view/photo/l/public/p1512562287.webp', '姜文', NULL, '8.9', NULL, '剧情/喜剧/动作/西部', NULL, '汉语普通话/四川话/山西话', '132', '3742360', '让子弹飞', NULL, '中国大陆/中国香港', '2010-12-16', NULL, '民国年间，花钱捐得县长的马邦德（葛优 饰）携妻（刘嘉玲 饰）及随从走马上任。途经南国某地，遭劫匪张麻子（姜文 饰）一伙伏击，随从尽死，只夫妻二人侥幸活命。马为保命，谎称自己是县长的汤 师爷。为汤师爷许下的财富所动，张麻子摇身一变化身县长，带着手下赶赴鹅城上任。有道是天高皇帝远，鹅城地处偏僻，一方霸主黄四郎（周润发 饰）只手遮天，全然不将这个新来的县长放在眼里。张麻子痛打了黄的武教头（姜武 饰），黄则设计害死张的义子小六（张默 饰）。原本只想赚钱的马邦德，怎么也想不到竟会被卷入这场土匪和恶霸的角力之中。鹅城上空愁云密布，血雨腥风在所难免……本片根据马识途的小说《夜谭十记》中的《盗官记》一章改编。', NULL, '2010');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (25, '周星驰/元秋/元华/黄圣依/梁小龙/陈国坤', NULL, NULL, 'https://img1.doubanio.com/view/photo/l/public/p2219011938.webp', '周星驰', NULL, '8.7', NULL, '喜剧/动作/犯罪/奇幻', NULL, '粤语/汉语普通话/手语', '100', '1291543', '功夫', NULL, '中国大陆/中国香港', '2004-12-23', NULL, '1940年代的上海，自小受尽欺辱的街头混混阿星（周星驰）为了能出人头地，可谓窥见机会的缝隙就往里钻，今次他盯上行动日益猖獗的黑道势力“斧头帮”，想借之大名成就大业。阿星假冒“斧头帮”成员试图在一个叫“猪笼城寨”的地方对居民敲诈，不想引来真的“斧头帮”与“猪笼城寨”居民的恩怨。“猪笼城寨”原是藏龙卧虎之处，居民中有许多身怀绝技者（元华、梁小龙等），他们隐藏于此本是为远离江湖恩怨，不想麻烦自动上身，躲都躲不及。而在观战正邪两派的斗争中，阿星逐渐领悟功夫的真谛。', NULL, '2004');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (26, '孔刘/郑有美/马东锡/金秀安/金义城', NULL, NULL, 'https://img1.doubanio.com/view/photo/l/public/p2360940399.webp', '延尚昊', NULL, '8.6', NULL, '动作/惊悚/灾难', NULL, '韩语', '118', '25986180', '釜山行', NULL, '韩国', '2016-5-13', NULL, '证券公司基金管理人石宇（孔侑 饰）光鲜精干，却也是个重利轻义之徒。妻子为此与之决裂，女儿秀安（金秀安 饰）则对如此自私的父亲越来越失望，决定前往釜山和母亲生活。在秀安生日这天，石宇抽出时间陪伴女儿登上开往釜山的特快列车。而与此同时，城市四处出现了极为可疑的暴动事件。政府极力洗白无法掩盖丧尸肆虐的事实，即便懵然无知的列车乘客也因为不速之客的到来而堕入恐慌绝望的地狱中。开车的刹那，一名感染者冲入车厢，而她很快尸变并对目光所及之处的健康人展开血腥屠杀。未过多久，丧尸便呈几何数爆发性地增长。石宇被迫和幸存者的乘客们在逼仄的空间中奋力求生。通往釜山的遥遥旅途布满杀机，危难时刻每个幸存者的人性也承受巨大的考验……', NULL, '2016');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (27, '萨姆·沃辛顿/佐伊·索尔达娜/西格妮·韦弗/史蒂芬·朗', NULL, NULL, 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2634997853.webp', '詹姆斯·卡梅隆', NULL, '8.8', NULL, '动作/科幻/冒险', NULL, '英语/西班牙语', '162', '1652587', '阿凡达', NULL, '美国', '2010-1-4', NULL, '战斗中负伤而下身瘫痪的前海军战士杰克·萨利（萨姆·沃辛顿 Sam Worthington 饰）决定替死去的同胞哥哥来到潘多拉星操纵格蕾丝博士（西格妮·韦弗 Sigourney Weaver 饰）用人类基因与当地纳美部族基因结合创造出的 “阿凡达” 混血生物。杰克的目的是打入纳美部落，外交说服他们自愿离开世代居住的家园，从而SecFor公司可砍伐殆尽该地区的原始森林，开采地下昂贵的“不可得”矿。在探索潘多拉星的过程中，杰克遇到了纳美部落的公主娜蒂瑞（佐伊·索尔达娜 Zoe Saldana 饰），向她学习了纳美人的生存技能与对待自然的态度。与此同时，SecFor公司的经理和军方代表上校迈尔斯（史蒂芬·朗 Stephen Lang 饰）逐渐丧失耐心，决定诉诸武力驱赶纳美人……本片采用3D技术拍摄，共耗资5亿美元制作发行，是电影史上最为昂贵的作品。本片荣获第82届奥斯卡最佳摄影、最佳视觉效果、最佳艺术指导等3项大奖。', NULL, '2010');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (28, '泰伊·谢里丹/奥利维亚·库克/本·门德尔森/马克·里朗斯', NULL, NULL, 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2516578307.webp', '史蒂文·斯皮尔伯格', NULL, '8.7', NULL, '动作/科幻/冒险', NULL, '英语/日语/汉语普通话', '140', '4920389', '头号玩家', NULL, '美国', '2018-3-30', NULL, '故事发生在2045年，虚拟现实技术已经渗透到了人类生活的每一个角落。詹姆斯哈利迪（马克·里朗斯 Mark Rylance 饰）一手建造了名为“绿洲”的虚拟现实游戏世界，临终前，他宣布自己在游戏中设置了一个彩蛋，找到这枚彩蛋的人即可成为绿洲的继承人。要找到这枚彩蛋，必须先获得三把钥匙，而寻找钥匙的线索就隐藏在詹姆斯的过往之中。韦德（泰尔·谢里丹 Tye Sheridan 饰）、艾奇（丽娜·维特 Lena Waithe 饰）、大东（森崎温 饰）和修（赵家正 饰）是游戏中的好友，和之后遇见的阿尔忒弥斯（奥利维亚·库克 Olivia Cooke 饰）一起，五人踏上了寻找彩蛋的征程。他们所要对抗的，是名为诺兰索伦托（本·门德尔森 Ben Mendelsohn 饰）的大资本家。', NULL, '2018');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (29, '斯科特·安第斯/瑞恩·波特/丹尼尔·海尼/ T·J·米勒/郑智麟', NULL, NULL, 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2614500883.webp', '唐·霍尔 / 克里斯·威廉姆斯', NULL, '8.7', NULL, '喜剧/动作/科幻/动画/冒险', NULL, '英语', '102', '11026735', '超能陆战队', NULL, '美国', '2015-2-28', NULL, '未来世界的超级都市旧京山（San Fransokyo），热爱发明创造的天才少年小宏，在哥哥泰迪的鼓励下参加了罗伯特·卡拉汉教授主持的理工学院机器人专业的入学大赛。他凭借神奇的微型磁力机器人赢得观众、参赛者以及考官的一致好评，谁知突如其来的灾难却将小宏的梦想和人生毁于一旦。大火烧毁了展示会场，而哥哥为了救出受困的卡拉汉教授命丧火场。身心饱受创伤的小宏闭门不出，哥哥生前留下的治疗型机器人大白则成为安慰他的唯一伙伴。原以为微型机器人也毁于火灾，谁知小宏和大白竟意外发现有人在某座废弃工厂内大批量地生产他的发明。稍后哥哥的朋友们弗雷德等人也加入进来，他们穿上小宏发明的超级战士战斗装备，和怀有险恶阴谋的神秘对手展开较量……', NULL, '2015');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (30, '陈思诚/严以宁/莲舟/张淳/程佳客', NULL, NULL, 'https://img1.doubanio.com/view/photo/m/public/p2523726459.webp', '陈思诚', NULL, '6.6', NULL, '喜剧/动作/悬疑', NULL, '汉语普通话/英语/日语/粤语', '121', '26698897', '唐人街探案2', NULL, '中国大陆', '2018-2-16', NULL, '唐仁（王宝强 饰）为巨额奖金欺骗秦风（刘昊然 饰）到纽约参加世界名侦探大赛，然而随着和世界各国侦探们啼笑皆非的较量，两人却发现了隐藏在这次挑战背后的更大秘密...', NULL, '2018');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (31, '朱一龙/杨恩又/王戈/刘陆/罗京民', NULL, 'Lighting Up The Stars', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2874262709.webp', '刘江江', NULL, '7.3', '151048', '剧情/家庭', 'tt1580159', '汉语', '112', '35460157', '人生大事', NULL, '中国大陆', '2022-6-24', NULL, '殡葬师莫三妹（朱一龙 饰）在刑满释放不久后的一次出殡中，遇到了孤儿武小文（杨恩又 饰），小文的出现，意外地改变了莫三妹对职业和生活的态度。', NULL, '2022');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (32, '刘亦菲/陈晓/柳岩/林允/徐海乔', NULL, '汴京梦华图', 'https://img9.doubanio.com/view/photo/s_ratio_poster/public/p2656327176.webp', '杨阳', NULL, '8.3', '588812', '剧情/爱情/古装', 'tt20770974', '汉语', '1600', '35231822', '梦华录', NULL, '中国大陆', '2022-6-2', NULL, '在钱塘开茶铺的赵盼儿（刘亦菲 饰）惊闻未婚夫、新科探花欧阳旭（徐海乔 饰）要另娶当朝高官之女，不甘命运的她誓要上京讨个公道。在途中她遇到了出自权门但生性正直的皇城司指挥顾千帆（陈晓 饰），并卷入江南一场大案，两人不打不相识从而结缘。赵盼儿凭借智慧解救了被骗婚而惨遭虐待的 “江南第一琵琶高手”宋引章（林允 饰）与被苛刻家人逼得离家出走的豪爽厨娘孙三娘（柳岩 饰），三位姐妹从此结伴同行，终抵东京，见识世间繁华。为了不被另攀高枝的欧阳旭从东京赶走，赵盼儿与宋引章、孙三娘一起历经艰辛，将小小茶坊一步步发展为东京最大的酒楼，揭露了负心人的真面目，收获了各自的真挚感情和人生感悟，也为无数平凡女子推开了一扇平等救赎之门。', NULL, '2022');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (33, '艾玛·汤普森/达利尔·麦克科马克/伊莎贝拉·拉夫兰德/莱斯·马巴莱卡/兰妮·贝阿雷', NULL, 'Good Luck to You, Leo Grande', 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2872943472.webp', '苏菲·海德', NULL, '8.3', '28460', '剧情/喜剧/情色', 'tt13352968', '英语', '97', '35235813', '祝你好运，里奥·格兰德', NULL, '英国', '2022-6-17', NULL, '一部关于爱、欲、和满足的喜剧片。艾玛·汤普森饰演的55岁寡妇Nancy Stokes迫切渴望冒险、人类联结和性，渴望她人生中第一次很棒的性爱。丈夫Robert给了她住宅、家庭和看起来像样的生活，然而他已经走了，如今Nancy觉得“连有些修女都比我性生活更 丰富”，她一向是行动派，且当过老师，相当有条理，于是她马上开始实施计划：做了一些研究之后，Nancy雇佣了一个20岁出头的性心理治疗师Leo Grande，准备重启对自己身体的认识。', NULL, '2022');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (34, '囧森瑟夫/瀚墨/陈浩/绿绮', NULL, '哪吒降世', 'https://img9.doubanio.com/view/photo/s_ratio_poster/public/p2563780504.webp', '饺子', NULL, '8.4', '1718413', '剧情/喜剧/动画/奇幻', 'tt10627720', '汉语', '110', '26794435', '哪吒之魔童降世', NULL, '中国大陆', '2019-7-13', NULL, '天地灵气孕育出一颗能量巨大的混元珠，元始天尊将混元珠提炼成灵珠和魔丸，灵珠投胎为人，助周伐纣时可堪大用；而魔丸则会诞出魔王，为祸人间。元始天尊启动了天劫咒语，3年后天雷将会降临，摧毁魔丸。太乙受命将灵珠托生于陈塘关李靖家的儿子哪吒身上。然而阴差阳错，灵珠和魔丸竟然被掉包。本应是灵珠英雄的哪吒却成了混世大魔王。调皮捣蛋顽劣不堪的哪吒却徒有一颗做英雄的心。然而面对众人对魔丸的误解和即将来临的天雷的降临，哪吒是否命中注定会立地成魔？他将何去何从？', NULL, '2019');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (35, '维果·莫腾森/马赫沙拉·阿里/琳达·卡德里尼/塞巴斯蒂安·马尼斯科/迪米特·D·马里诺夫', NULL, '绿簿旅友', 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2549177902.webp', '彼得·法雷里', NULL, '8.9', '1482779', '剧情/喜剧/音乐/传记', 'tt6966692', '英语/意大利语/俄语/德语', '130', '27060077', '绿皮书', NULL, '美国/中国大陆', '2019-3-1', NULL, '托尼（维果·莫腾森 Viggo Mortensen 饰）是一个吊儿郎当游手好闲的混混，在一家夜总会做侍者。这间夜总会因故要停业几个月，可托尼所要支付的房租和生活费不会因此取消，所以他的当务之急是去寻找另一份工作来填补这几个月的空缺。在这个节骨眼上，一位名叫唐雪莉（马赫沙拉·阿里 Mahershala Ali 饰）的黑人钢琴家提出雇佣托尼。唐雪莉即将开始为期八个星期的南下巡回演出，可是，那个时候南方对黑人的歧视非常的严重，于是托尼便成为了唐雪莉的司机兼保镖。一路上，两人迥异的性格使得他们之间产生了很多的矛盾，与此同时，唐雪莉在南方所遭受的种种不公平的对待也让托尼对种族歧视感到深恶痛绝', NULL, '2019');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (36, '蒂姆·罗宾斯/摩根·弗里曼/鲍勃·冈顿/威廉姆·赛德勒/克兰西·布朗', NULL, '月黑高飞', 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p480747492.webp', '弗兰克·德拉邦特', NULL, '9.7', '2645331', '剧情/犯罪', 'tt0111161', '英语', '142', '1292052', '肖申克的救赎', NULL, '美国', '1994-10-14', NULL, '一场谋杀案使银行家安迪（蒂姆?罗宾斯 Tim Robbins 饰）蒙冤入狱，谋杀妻子及其情人的指控将囚禁他终生。在肖申克监狱的首次现身就让监狱“大哥”瑞德（摩根?弗里曼 Morgan Freeman 饰）对他另眼相看。瑞德帮助他搞到一把石锤和一幅女明星海报，两人渐成患难 之交。很快，安迪在监狱里大显其才，担当监狱图书管理员，并利用自己的金融知识帮助监狱官避税，引起了典狱长的注意，被招致麾下帮助典狱长洗黑钱。偶然一次，他得知一名新入狱的小偷能够作证帮他洗脱谋杀罪。燃起一丝希望的安迪找到了典狱长，希望他能帮自己翻案。阴险伪善的狱长假装答应安迪，背后却派人杀死小偷，让他唯一能合法出狱的希望泯灭。沮丧的安迪并没有绝望，在一个电闪雷鸣的风雨夜，一场暗藏几十年的越狱计划让他自我救赎，重获自由！老朋友瑞德在他的鼓舞和帮助下，也勇敢地奔向自由。', NULL, '1994');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (37, '莱昂纳多·迪卡普里奥/凯特·温丝莱特/比利·赞恩/凯西·贝茨/弗兰西丝·费舍', NULL, '铁达尼号', 'https://img9.doubanio.com/view/photo/s_ratio_poster/public/p457760035.webp', '詹姆斯·卡梅隆', NULL, '9.4', '1948231', '剧情/爱情/灾难', 'tt0120338', '英语/意大利语/德语/俄语', '194', '1292722', '泰坦尼克号 Titanic', NULL, '美国/墨西哥/澳大利亚/加拿大', '1998-4-3', NULL, '1912年4月10日，号称 “世界工业史上的奇迹”的豪华客轮泰坦尼克号开始了自己的处女航，从英国的南安普顿出发驶往美国纽约。富家少女罗丝（凯特?温丝莱特）与母亲及未婚夫卡尔坐上了头等舱；另一边，放荡不羁的少年画家杰克（莱昂纳多·迪卡普里奥）也在码头的一场赌博中赢得了下等舱的船票。罗丝厌倦了上流社会虚伪的生活，不愿嫁给卡尔，打算投海自尽，被杰克救起。很快，美丽活泼的罗丝与英俊开朗的杰克相爱，杰克带罗丝参加下等舱的舞会、为她画像，二人的感情逐渐升温。1912年4月14日，星期天晚上，一个风平浪静的夜晚。泰坦尼克号撞上了冰山，“永不沉没的”泰坦尼克号面临沉船的命运，罗丝和杰克刚萌芽的爱情也将经历生死的考验', NULL, '1998');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (38, '汤姆·汉克斯/罗宾·怀特/加里·西尼斯/麦凯尔泰·威廉逊/莎莉·菲尔德', NULL, '福雷斯特·冈普', 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2372307693.webp', '罗伯特·泽米吉斯', NULL, '9.5', '1988263', '剧情/爱情', 'tt0109830', '英语', '142', '1292720', '阿甘正传', NULL, '美国', '1994-6-23', NULL, '阿甘（汤姆·汉克斯 饰）于二战结束后不久出生在美国南方阿拉巴马州一个闭塞的小镇，他先天弱智，智商只有75，然而他的妈妈是一个性格坚强的女性，她常常鼓励阿甘“傻人有傻福”，要他自强不息。阿甘像普通孩子一样上学，并且认识了一生的朋友和至爱珍妮（罗宾·莱特·潘 饰），在珍妮 和妈妈的爱护下，阿甘凭着上帝赐予的“飞毛腿”开始了一生不停的奔跑。阿甘成为橄榄球巨星、越战英雄、乒乓球外交使者、亿万富翁，但是，他始终忘不了珍妮，几次匆匆的相聚和离别，更是加深了阿甘的思念。', NULL, '1994');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (39, '王传君/周一围/谭卓/章宇', NULL, '中国药神', 'https://img9.doubanio.com/view/photo/s_ratio_poster/public/p2561305376.webp', '文牧野', NULL, '9', '1933646', '剧情/喜剧', 'tt7362036', '汉语普通话/英语/上海话/印地语', '117', '26752088', '我不是药神', NULL, '中国大陆', '2018-6-30', NULL, '普通中年男子程勇（徐峥 饰）经营着一家保健品店，失意又失婚。不速之客吕受益（王传君 饰）的到来，让他开辟了一条去印度买药做“代购”的新事业，虽然困难重重，但他在这条“买药之路”上发现了商机，一发不可收拾地做起了治疗慢粒白血病的印度仿制药独家代理商。赚钱的同时，他也认识了几个病患及家属，为救女儿被迫做舞女的思慧（谭卓 饰）、说一口流利“神父腔”英语的刘牧师（杨新鸣 饰），以及脾气暴烈的“黄毛”（章宇 饰），几个人合伙做起了生意，利润倍增的同时也危机四伏。程勇昔日的小舅子曹警官（周一围 饰）奉命调查仿制药的源头，假药贩子张长林（王砚辉 饰）和瑞士正牌医药代表（李乃文 饰）也对其虎视眈眈，生意逐渐变成了一场关于救赎的拉锯战。', NULL, '2018');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (40, '入野自由/夏木真理/菅原文太/中村彰男', NULL, '神隐少女(台)', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2557573348.webp', '宫崎骏', NULL, '9.4', '2064463', '剧情/动画/奇幻', 'tt0245429', '日语', '125', '1291561', '千与千寻 千と千尋の神隠し', NULL, '日本', '2019-6-21', NULL, '千寻和爸爸妈妈一同驱车前往新家，在郊外的小路上不慎进入了神秘的隧道——他们去到了另外一个诡异世界—一个中世纪的小镇。远处飘来食物的香味，爸爸妈妈大快朵颐，孰料之后变成了猪！这时小镇上渐渐来了许多样子古怪、半透明的人。千寻仓皇逃出，一个叫小白的人救了他，喂了她阻止身体消 失的药，并且告诉她怎样去找锅炉爷爷以及汤婆婆，而且必须获得一份工作才能不被魔法变成别的东西。千寻在小白的帮助下幸运地获得了一份在浴池打杂的工作。渐渐她不再被那些怪模怪样的人吓倒，并从小玲那儿知道了小白是凶恶的汤婆婆的弟子。一次，千寻发现小白被一群白色飞舞的纸人打伤，为了救受伤的小白，她用河神送给她的药丸驱出了小白身体内的封印以及守封印的小妖精，但小白还是没有醒过来。为了救小白，千寻又踏上了她的冒险之旅', NULL, '2019');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (41, '石川瑠华/青木柚/前田旺志郎/中田青渚/仓悠贵/村上淳', NULL, '海边的女孩', 'https://img9.doubanio.com/view/photo/s_ratio_poster/public/p2669945696.webp', '上田笃司', NULL, '6.5', '320', '剧情/爱情', 'tt14820290', '日语', '107', '35417835', '错位的青春', NULL, '日本', '2021-8-20', NULL, '海辺の小さな街で暮らす中学生?小梅は、憧れの先輩?三崎に手ひどく振られたショックから、かつて自分のことを好きだと言ってくれた内向的な同級生?磯辺と関係を持つ。初めは興味本位だったが、何度も体を重ねる2人。やがて小梅は恋愛対象として見ていなかった磯辺への思いを募らせるようになる。逆に、恋焦がれていた小梅との関係を断ち切ろうとする磯辺。そして彼は、いじめを苦に自殺した兄への贖罪のため、ある行動に出る。', NULL, '2021');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (42, '徐峥/马伊琍/吴越/倪虹洁/周野芒', NULL, 'B for Busy', 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2772925591.webp', '邵艺辉', NULL, '8.1', '606231', '剧情/喜剧/爱情', 'tt16606348', '汉语普通话 / 沪语', '112', '35376457', '爱情神话', NULL, '中国大陆', '2021-12-24', NULL, '离异多年的画家老白（徐峥 饰）经朋友介绍认识了同样离异，带着一个孩子的李小姐（马伊琍 饰），老白对李小姐一见倾心，可是李小姐似乎对这段感情有所顾虑，一直止步不前。老白在家里开设了美术班，教成年人画画，格洛瑞亚（倪虹洁 饰）是他的学生之一。热情奔放的格洛瑞亚虽然已经结婚，但是丈夫远在国外常年不回家，她似乎对老白有着超越了师生的感情。蓓蓓（吴越 饰）是老白的前妻，两人虽然已经离婚多年，而且是蓓蓓背叛了老白，但老白的母亲还是希望两人有朝一日可以复婚。在上海的弄堂里，一个男人和三个女人的故事徐徐拉开序幕', NULL, '2021');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (43, '菅田将晖/有村架纯/细田佳央太/清原果耶', NULL, '她和他的恋爱花期', 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2868462052.webp', '土井裕泰', NULL, '8.6', '439294', '剧情/爱情', 'tt11219254', '日语', '124', '34874432', '花束般的恋爱', NULL, '日本', '2021-1-29', NULL, '山音麦 (菅田将晖 饰) 和八谷绢 (有村架纯 饰) 因错过尾班车而相遇，在深夜咖啡馆聊起文学、电影和音乐，喜好竟奇蹟地相似，二人瞬间坠入爱河。他们毕业后开始一边兼职工作，一边开始同居生活，更一起养拾回来的街猫，最大的目标就是维持俩口子的日常。 但时日流逝，不想改变也得改变。他失约于《牯岭街》， 她独赏郭利斯马基黑色幽默。五年盛放，世上唯一的花未必结果，爱情若只如初见。《垫底辣妹》《借着雨点说爱你》导演土井裕泰联同《东京爱的故事》神级编剧坂元裕二，强调真实生活感的浪漫，菅田将晖和有村架纯衬到绝合演，谱出这代最揪心的爱的故事。', NULL, '2021');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (44, '屈楚萧/张婧仪/孙宁/张垚/李嘉灏', NULL, '10年女友', 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2872957602.webp', '沙漠', NULL, '6', '118448', '剧情/爱情', 'tt14546082', '汉语普通话', '105', '25881778', '我要我们在一起', NULL, '中国大陆', '2022-5-20', NULL, '你有没有爱过一个人，曾经拼了命，只为和TA在一起。十年前，差生吕钦扬当众告白凌一尧，两人从校园步入社会，为了让她幸福，他不惜以命相搏。然而金钱、房子、婚姻等现实的考验，却将两人越推越远。十年长跑，他们能否还记得曾经刻在心底的约定：我要我们在一起。', NULL, '2022');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (45, '赵又廷/邓伦/王子文/春夏/汪铎', NULL, '阴阳师', 'https://img3.doubanio.com/view/photo/s_ratio_poster/public/p2622484010.webp', '郭敬明', NULL, '5', '195551', '爱情/奇幻', 'tt11454718', '汉语普通话', '132', '30284141', '晴雅集', NULL, '中国大陆', '2021-2-5', NULL, '天都城内频频出现怪象，法师们从各方赶来，试图找到真相。阴阳师晴明（赵又廷 饰）在探寻真相的过程中，与武士博雅（邓伦 饰）、法师泷夜（春夏 饰）、鹤守月（汪铎 饰）相识，四人决定联手展开调查。深宫内，公主（王子文 饰）背负着巨大秘密，深陷这场迷案中...却未曾料到，这背 后暗藏一段哀伤的往事。一场危机悄然来临，在紧急关头，有人为知己、有人为心上人、有人为世间安宁，大家为了守护心中所爱，拼死一战……', NULL, '2021');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (46, '李鸿其/李一桐/范伟/张超/罗辑', NULL, '穿越时空的爱', 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2614225081.webp', '姚婷婷', NULL, '5.3', '64318', '爱情/奇幻', 'tt11285152', '汉语普通话', '115', '26661193', '我在时间尽头等你', NULL, '中国大陆', '2020-8-25', NULL, '林格（李鸿其 饰）人生所有美好的记忆，都与邱倩（李一桐 饰）有关。一场意外、一块神奇的表彻底改变了两人的人生......时空不断被重置，唯一不变的，是爱你如初的心。', NULL, '2020');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (47, '邱泽/许玮甯/蔡振南/钟欣凌/蓝苇华', NULL, '当男人恋爱时', 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2656407373.webp', '殷振豪', NULL, '6.3', '97329', '爱情', 'tt14428606', '闽南语 / 汉语普通话', '115', '35198827', '当男人恋爱时', NULL, '中国台湾/中国香港', '2021-4-1', NULL, '当男人恋爱时会是什么样子？以讨债为生的小混混阿成（邱泽 饰）看似凶狠却本性善良，遇到了欠债人的女儿浩婷（许玮甯 饰）后，对浩婷一见钟情的阿成开始变得温柔又幼稚，笨拙地为浩婷打点一切。就在浩婷终于卸下防备接受追求的时候，他们之间却遭遇了接二连三的变故，阿成默默承受着一切。阿成最终也没有忘记告诉浩婷那句藏在心底的表白：第一次见到你，我就喜欢你了。', NULL, '2021');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (48, '马思纯/俞飞鸿/彭于晏/张钧甯/范伟', NULL, '沉香屑·第一炉香', 'https://img3.doubanio.com/view/photo/s_ratio_poster/public/p2700127550.webp', '许鞍华', NULL, '5', '117115', '剧情/爱情', 'tt10415576', '汉语普通话 / 英语 / 葡萄牙语', '144', '27102476', '第一炉香', NULL, '中国大陆', '2021-10-22', NULL, '电影根据张爱玲的同名中篇小说改编。葛薇龙（马思纯 饰）是一位单纯而又善良的女学生，为了躲避战火，她和家人逃难来到了香港，之后，受生活所迫，葛薇龙的家人决定返回故乡，打算继续求学的葛薇龙只身一人留在了异乡，并且投靠了生活在这里，在很早之前就和家族断绝了关系的姑母梁太太（俞飞鸿 饰）。虽然外头对于梁太太的流言蜚语满天飞，但是葛薇龙自认为并不会被这个神秘的女人所影响。可随着时间的推移，在梁太太的带领之下，葛薇龙品尝到了奢靡生活所带来的甜头，并且逐渐沉迷无法自拔。在梁太太毁灭了她对于爱情的美好幻想之后，一位名叫乔琪乔（彭于晏 饰）的公子哥出现在了葛薇龙的身边，令葛薇龙重新燃起了对于爱情的渴望', NULL, '2021');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (49, '黄渤/贾玲/朱一龙/徐帆/高亚麟/吴彦姝', NULL, '江城子·在武汉', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2801718909.webp', '薛晓路', NULL, '6', '106507', '剧情/爱情', 'tt16287604', '汉语普通话 / 武汉方言', '124', '35307456', '穿过寒冬拥抱你', NULL, '中国大陆', '2021-12-31', NULL, '2020年1月23日，农历腊月二十九，武汉宣布封城。本片聚焦这一特殊时刻，以温情和治愈为基调，刻画了普通市民守望相助、真挚动人的爱情群像。快递员阿勇（黄渤 饰），骑手武哥（贾玲 饰），钢琴老师叶子扬（朱一龙 饰），中年夫妇刘亚兰（徐帆 饰）和李宏宇（高亚麟 饰），退休老医生谢咏琴（吴彦姝 饰）和餐厅老板沛爷（许绍雄 饰），实习护士夏晓（周冬雨 饰），滞留旅客张哲（刘昊然 饰）和君君（乔欣 饰）。疫情下，爱给了他们挺身而出的勇气和力量。他们以笑做盾，以爱为矛，冲破凛冽寒冬和恐惧，勇敢拥抱。爱在武汉的烟火气下蔓延，抵御一切苦难，最终见证奇迹。', NULL, '2021');
INSERT INTO `api_test_popular_movies` (`id`, `ACTORS`, `ACTOR_IDS`, `ALIAS`, `COVER`, `DIRECTORS`, `DIRECTOR_IDS`, `DOUBAN_SCORE`, `DOUBAN_VOTES`, `GENRES`, `IMDB_ID`, `LANGUAGES`, `MINS`, `MOVIE_ID`, `NAME`, `OFFICIAL_SITE`, `REGIONS`, `RELEASE_DATE`, `SLUG`, `STORYLINE`, `TAGS`, `YEAR`) VALUES (50, '凯瑟琳·沃特斯顿/凡妮莎·柯比/克里斯托弗·阿波特/卡西·阿弗莱克', NULL, '未来世界', 'https://img3.doubanio.com/view/photo/s_ratio_poster/public/p2632936000.webp', '莫娜·法斯特欧德', NULL, '8.2', '30794', '剧情/爱情/同性', 'tt9738716', '英语', '105', '30454679', '打开心世界', NULL, '美国', '2020-9-6', NULL, '故事发生在19世纪的美国东北部，艾比盖尔（凯瑟琳·沃特森 Katherine Waterston 饰）和丈夫戴亚（卡西·阿弗莱克 Casey Affleck 饰）在这里经营着一座农场，繁重的农活让艾比盖尔和丈夫之间没有任何的交流，不久之前两人的女儿因病去世的事件也让这对夫妻之间的感情更加的疏离。某日，农场里来了一对小夫妻——塔利（凡妮莎·柯比 Vanessa Kirby 饰）和芬妮（克里斯托弗·阿波特 Christopher Abbott 饰），塔利的处境和艾比盖尔差不过，在家庭中也处于弱势的地位，她因为无法怀孕而遭到丈夫的冷待。两个内心里伤痕累累的女人一拍即合，很快就培养出了真挚的友谊，这友谊随着时间渐渐发酵，散发出了爱情的醇香。', NULL, '2020');
